package com.ddt.pay_library.bean;import java.util.Map;import android.text.TextUtils;/** * 支付宝的结果回调对象 */public class PayResult {	private String resultStatus;	private String result;	private String memo;	public PayResult(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			if (TextUtils.equals(key, "resultStatus")) {				resultStatus = rawResult.get(key);			} else if (TextUtils.equals(key, "result")) {				result = rawResult.get(key);			} else if (TextUtils.equals(key, "memo")) {				memo = rawResult.get(key);			}		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}	public void setResultStatus(String resultStatus) {		this.resultStatus = resultStatus;	}	public void setResult(String result) {		this.result = result;	}	public void setMemo(String memo) {		this.memo = memo;	}}